DROP DATABASE IF EXISTS `ApnaBazaar`;
CREATE DATABASE `ApnaBazaar`; 
USE `ApnaBazaar`;

SET character_set_client = utf8mb4 ;

create table Customer (
	CustomerID INT,
	CustomerName VARCHAR(50),
	EmailID VARCHAR(50),
	Address VARCHAR(50),
    primary key (`CustomerID`)
);




create table Category (
	CategoryID INT,
	CategoryName VARCHAR(50),
	Description text,
    PRIMARY KEY (`CategoryID`)
);

create table Offers (
	OfferID INT,
	OfferName VARCHAR(50),
	OfferType VARCHAR(50),
	Description TEXT,
    PRIMARY KEY (`OfferID`)
);

create table Product (
	ProductID INT,
	ProductName VARCHAR(50),
	ProductCategoryID INT,
	BrandName VARCHAR(50),
	mfgDate DATE,
    primary key (`ProductID`)
);


create table Salesman (
	SalesmanID INT,
	SalesmanName VARCHAR(50),
	Address VARCHAR(50),
	EmailID VARCHAR(50),
	City VARCHAR(50),
	Region VARCHAR(50),
    primary key (`SalesmanID`)
);

create table Sales (
	SalesID INT,
	SalesType VARCHAR(50),
	DailySales INT,
    primary key (`SalesID`)
);



create table Feedback (
	FeedbackID INT,
	FeedbackType VARCHAR(50),
	Description VARCHAR(50),
    primary key (`FeedbackID`)
);


create table Coupons (
	CouponID INT,
	CustomerName VARCHAR(50),
	CouponType VARCHAR(50),
    primary key (`CouponID`)
);

create table Bill (
	BillNumber INT,
	CustomerID INT,
	SalesmanID INT,
	EmailID VARCHAR(50),
	ProductID INT,
	UnitPrice INT,
	Qty INT,
	Discount INT,
	DeliveryCharge INT,
	BillingAddress VARCHAR(50),
	BillDate DATE,
	BillTime VARCHAR(50),
	TotalAmount INT,
    PRIMARY KEY (`BillNumber`),
    KEY `FK_CustomerID` (`CustomerID`),
    CONSTRAINT `FK_CustomerID` FOREIGN KEY (`CustomerID`) REFERENCES `Customer` (`CustomerID`) ON DELETE RESTRICT ON UPDATE CASCADE,
    KEY `FK_SalesmanID` (`SalesmanID`),
    CONSTRAINT `FK_SalesmanID` FOREIGN KEY (`SalesmanID`) REFERENCES `Salesman` (`SalesmanID`) ON DELETE RESTRICT ON UPDATE CASCADE,
    KEY `FK_ProductID` (`ProductID`),
    CONSTRAINT `FK_ProductID` FOREIGN KEY (`ProductID`) REFERENCES `Product` (`ProductID`) ON DELETE RESTRICT ON UPDATE CASCADE
);


create table Stores (
	StoresID INT,
	StoresName VARCHAR(50),
	Address VARCHAR(50),
	EmailID VARCHAR(50),
	City VARCHAR(50),
	Region VARCHAR(50),
    primary key (`StoresID`)
);



create table Transaction (
	TransactionID INT,
	BillNumber INT,
	TransactionDate DATE,
	TransactionTime VARCHAR(50),
	TransactionType VARCHAR(50),
    primary key (`TransactionID`),
    KEY `FK_BillNumber` (`BillNumber`),
    CONSTRAINT `FK_BillNumber` FOREIGN KEY (`BillNumber`) REFERENCES `Bill` (`BillNumber`) ON DELETE RESTRICT ON UPDATE CASCADE
);


create table Cart (
	CartID INT,
	CartStatus VARCHAR(50),
	ProductQty INT,
	BillNumber INT,
    primary key (`CartID`),
    KEY `FRK_BillNumber` (`BillNumber`),
    CONSTRAINT `FRK_BillNumber` FOREIGN KEY (`BillNumber`) REFERENCES `Bill` (`BillNumber`) ON DELETE RESTRICT ON UPDATE CASCADE
);


create table Admin (
	AdminID INT,
	AdminName VARCHAR(50),
    primary key (`AdminID`)
);


create table SubCategory (
	SubCategoryID INT,
	SubCategoryName VARCHAR(50),
	Description VARCHAR(50),
    primary key (`SubCategoryID`)
);


